frame_eqs_eel:
flen = 512; //Wave Punkte
reg00 = FLen;
SLen = FLen/2;
dec_m = pow(.94, 30/fps);

//Volume
n = 0; vol = 0;
loop (SLen,
  vol = vol + pow(gmegabuf(n),2)/FLen;  
  n = n+1;
);
vol = sqrt(vol)/2;
vol_ = vol_*dec_m + (1-dec_m)* (vol);

//switches
lev1 = lev1 * dec_m + (1-dec_m) * gmegabuf(1);
lev2 = lev2 * dec_m + (1-dec_m) * gmegabuf(20);
lev3 = lev3 * dec_m + (1-dec_m) * gmegabuf(50);
lev4 = lev4 * dec_m + (1-dec_m) * gmegabuf(100);
sw1 = sw1 + (lev1-gmegabuf(1)>0)/fps;
sw2 = sw2 + (lev2-gmegabuf(20)>0)/fps;
sw3 = sw3 + (lev3-gmegabuf(50)>0)/fps;
sw4 = sw4 + (lev4-gmegabuf(100)>0)/fps;

dif = (1+sin(sw3/4))*16+2;
ofs = sin(sw2/3)*8;
n = 0; k1 = frame*0; 
loop (SLen,
      m1 = (n)%SLen;
      m2 = (n+ofs)%SLen;

      k = (m1+dif)%SLen;
      i = (SLen+m2-dif)%SLen;

      in1 = (gmegabuf(m1)-gmegabuf(k))/pow(vol_+.03,.8)*1.2;
      in2 = (gmegabuf(m2)-gmegabuf(i))/pow(vol_+.03,.8)*1.2;

      dec = if (pow(gmegabuf(2000+n),2) + pow(gmegabuf(2000+FLen+n),2)> in1*in1+in2*in2,.8,.94) ;
      dec = pow (dec, 30/fps); 

      gmegabuf(2000+n) =       gmegabuf(2000+n)*dec      +(1-dec)* in1;
      gmegabuf(2000+FLen+n) =  gmegabuf(2000+FLen+n)*dec +(1-dec)* in2;
      n=n+1;
);

q1 = (bass+treb+mid)/3;
q2 = (bass_att+treb_att+mid_att)/3;
q3 = vol_;
q11 = sw1;
q12 = sw2;
q13 = sw3;
