warp:
shader_body { 
 vec3 ret_1;
 ret_1 = texture (sampler_main, uv).xyz;
 ret_1 = (ret_1 + ((ret_1 - 
   ((texture (sampler_blur2, uv).xyz * scale2) + bias2)
 ) * 0.3));
 ret_1 = (ret_1 * 0.9);
 ret_1 = (ret_1 + ((
   ((texture (sampler_noise_lq, ((
     (uv_orig * texsize.xy)
    * 
     (texsize_noise_lq.zw * 0.4)
   ) + rand_frame.xy)).xyz - 0.5) / 256.0)
  * 122.0) * clamp (
   (treb_att - 1.0)
 , 0.0, 1.0)));
 ret_1 = mix (ret_1, vec3(dot (ret_1, vec3(0.32, 0.49, 0.29))), vec3(0.2, 0.2, 0.2));
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz + ((
   (texture (sampler_blur1, uv).xyz * scale1)
  + bias1) * 0.4));
 vec3 tmpvar_2;
 tmpvar_2 = pow (ret_1, vec3(0.5, 0.8, 1.7));
 ret_1 = tmpvar_2;
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = tmpvar_2;
 ret = tmpvar_3.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret.xyz = tex2D( sampler_main, uv ).xyz;

      // take the difference between the crisp and blurred images,
      // then add it back into the image.  Creates spots and stripes over time.
      ret.xyz += (ret.xyz - GetBlur2(uv))*0.3;
      ret.xyz *= 0.9;

      // add noise:
      float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw * 0.4 + rand_frame.xy;
      ret.xyz += (tex2D(sampler_noise_lq, dither_uv).xyz-0.5)/256.0 * 122 * saturate(treb_att-1);

      // desaturate over time, to keep the globs white
      ret.xyz = lerp(ret.xyz, lum(ret.xyz), 0.2);
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;
    ret += GetBlur1(uv)*0.4;

    // palettize:
    ret = pow(ret, float3(0.5, 0.8, 1.7));

