waves.point_eqs_str:
//init
zs = if(below(zs,1),9,zs);

zs = if(above(zs, 1300),1, zs);

speed = bass*0.002;

xcoef = equal(q4,0)*2 + equal(q4,1)*2.1  + equal(q4,2)*2.2 + equal(q4,3)*2.3 + equal(q4,4)*2.4 + equal(q4,5)*2.5;

//sum
zs = zs + tan(q1*0.015)*speed;


count = count + 0.0001;
count = if(above(count,6.24),0,count);

//project
x = 0.1*cos(q1*zs*2 + 0.1);
y = 0.1*sin(q1*zs*1 + 0.1);

//rotate
x = x*cos(-q1*3)*0.8 - y*sin(-q1*3)*0.8;
y = x*sin(-q1*3) + y*cos(-q1*3);

//move
x = x + q6 + 0.5;
y = y + q7 + 0.5;

r = 0.5 + 0.5*sin(q1*1.2 + x + x);
g = 0.5 + 0.5*sin(q1*1.5 + x + y);
b = 0.5 + 0.5*sin(q1*1.36 + y + y);

