{

    float3 txr = float3(uv,q1*0.1);
    float2  uv2 =  uv;
    uv2 = uv + texsize.zx*(q3,q3);
    float3 color = tex2D(sampler_noise_lq, uv2) - 0.5;
    color += tex2D(sampler_noise_mq, uv2) - 0.5;
    color += tex2D(sampler_noise_hq, uv2) - 0.5;

    float2x2 rot = { cos(q9), sin(q9),
