   for(int i=0;i<4;i++)
    output += samples[i].w*tex2D(sampler_main, uv   +
      float2(samples[i].x*(1/texsize.x),
             samples[i].y*(1/texsize.y)))*0.2;


    ret = tex2D( sampler_main, (
                               ((mul((uv-0.5),rot)*(0.97 + rad*0.02)) + 0.5
                                + GetBlur1((output+ color - 0.5)*0.9 + 0.5)*0.02  + 0.0) - 0.5
