waves.point_eqs_eel:
xspout=.5;
yspout=-.01;

pphase=9999*sample*sample*.0001;
pphase2=.1+((sample*3349*sample)%100)*.01;
pheight=((sample*9893)%100)*.002;
yheight=((sample*1231*sample)%100)*.01;

r=((sample*5454)%100)*.01*abs(sin(time*.25));
g=((sample*9954)%100)*.01;

xoffset1=(cos((time*pphase2)+pphase)*pheight);
xoffset2=-1*(cos((time*pphase2)+pphase)*pheight);

lrorient=if(below(cos((time*pphase2)+pphase),cos(((time-.1)*pphase2)+pphase)),0,1);

px=if(equal(lrorient,0),xspout-pheight+xoffset1,xspout+pheight-xoffset2);

py=yspout+(abs(sin((time*pphase2)+pphase))*yheight);

x=px;
y=py;

pphase=(sample*5671*cos(time*.0001));
pheight=((sample*7654)%100)*.005*sin(time*.2);

//xspout=.5;
xspout=if(below(abs(sin(time*.2)),.005),.3+(rand(40)*.01),xspout);
//yspout=.5;
yspout=if(below(abs(sin(time*.2)),.005),.3+(rand(40)*.01),yspout);

px=xspout+(cos(time+pphase)*pheight);
py=yspout+(sin(time+pphase)*pheight);

x=px;
y=py;
a=abs(sin(time*.2)*.3+(treb_att*.1));

r=treb*2;

n=sample*6.283;
phs=-sample * 0.2;
tm=q1 + phs*4;

flip=flip+1;
flip=flip*below(flip,2);

xp=0;
yp=(flip*0.1-0.05)*(sample);
zp=0;

//spin;
ang=tm*20 + sin(tm*76 + time*4)*0.4;
sa=sin(ang);ca=cos(ang);
xr=xp*sa + yp*ca;
yr=xp*ca - yp*sa;
zr=zp;

xp=xr;
yp=yr + 0.05 + (sin(tm)*0.5 + 0.5)*0.2 + 0.05;
zp=zr;

//wrist movement;
ang=sin(tm*2);

xq=xp;
sa=sin(ang);ca=cos(ang);
yq=yp*sa + zp*ca;
zq=yp*ca - zp*sa;


ang=tm*8;
sa=sin(ang);ca=cos(ang);
xp=xq*sa + yq*ca;
yp=xq*ca - yq*sa;
zp=zq;

//forearm movement;
zp=zp-0.3;
ang=3.14 + sin(tm*2 - 0.5)*2.5;
xq=xp;
sa=sin(ang);ca=cos(ang);
yq=yp*sa + zp*ca;
zq=yp*ca - zp*sa;

//upper arm twist
ang=-1.0 + cos(tm*3 + 0.5);
xp=xq*sa + yq*ca;
yp=xq*ca - yq*sa;
zp=zq;

//upper arm outward;
zp=zp-0.35;
ang=cos(tm*1)*1.75 - 1.05;
sa=sin(ang);ca=cos(ang);
xq=xp*sa + zp*ca;
yq=yp;
zq=xp*ca - zp*sa;

//upper arm up down;
ang=cos(tm);
xp=xq;
sa=sin(ang);ca=cos(ang);
yp=yq*ca - zq*sa;
zp=yq*sa + zq*ca;

//xp=xq;yp=yq;zp=zq;


//project into screenspace and draw on screen
zp=zp+1.5;
xs=xp/zp;
ys=yp/zp;

x=xs+0.5;
y=ys*1.3+0.5;


a=(1-sample);

waves.point_eqs_str:
sample=pow(sample,.85);

c1=6.2831*t1/(1.1-sample);
c=cos(c1);
s=sin(c1);
c2=sqrt(1-sample);

ox=t7-.5*c2*c;
oy=t8-.625*c2*s;

x=t7+((ox-t7)*cos(t2)-(oy-t8)*sin(t2));
y=t8+((ox-t7)*sin(t2)+(oy-t8)*cos(t2));

inv=1/sqr(1.1-sample);
fdx=-.25*pow(1-sample,-.5)*c-c2*s*3.1415*t1*inv;
fdy=-.3125*c2*s+3.9268*c2*c*t1*inv;
invMag=1/sqrt(sqr(fdx)+sqr(fdy));
fdx=fdx*invMag;
fdy=fdy*invMag;

rdx=fdx*cos(1.5707+t2)-fdy*sin(1.5707+t2);
rdy=fdx*sin(1.5707+t2)+fdx*cos(1.5707+t2);
tt=rand(100)/100;
x=x+rdx*(.15*tt)*(1-sample);
y=y+rdy*(.15*tt)*(1-sample);

x=x*.55;
y=(y-.5)*.55+.5;

r=sample;
g=1-sample;
b=1;
a=below(sample,.995);


y=y+t3;

