frame_eqs_eel:
wave_x = wave_x + 0.1*sin(5*bass);
wave_y = wave_y + 0.1*sin(5*bass);
wave_r = wave_r + 1;
wave_g =wave_g + rand(2);
wave_b = 0;
rot = rot + if (above(bass,0), 0.02*sin(6*bass) - 0.025*cos(4.75*bass), 0);

warp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 tmpvar_1.xyz = (texture (sampler_main, (uv + (
   (texture (sampler_main, (mix (uv, uv_orig, vec2(-1.0, -1.0)) + texsize.zw)).xy - 0.37)
  * 
   (0.005 + (0.025 * clamp ((treb - 1.0), 0.0, 1.0)))
 ))).xyz - 0.004);
 ret = tmpvar_1.xyz;
}

warp_hlsl:
shader_body
{
    // PAINTERLY EFFECT:
    float t = 0.005 + 0.025*saturate(treb-1);
    float2 uv2 = uv;
    float2 delta2 = texsize.zw*float2(1,1);
    float3 blurry_color = tex2D( sampler_main, 
        lerp(uv,uv_orig,-1) + delta2 ).xyz;
    uv2.xy += (blurry_color.xy-0.37) * t;

    // sample previous frame
    ret = tex2D( sampler_main, uv2 ).xyz;

    // darken over time
    ret -= 0.004;    
}
