frame_eqs_eel:
vol = bass*8 + mid*5 + treb*3;
m = m*0.97 + vol*0.08;
monitor = vol;
beat = above(vol,res)*above(vol,m)*above(vol,16);
diff = (1-beat)*diff + beat*(vol-res);
res = beat*(vol + m*0.1) + (1-beat)*(res -  (0.1+diff*0.02)*60/fps);
res = max(0,res);


d_x = if(beat, (rand(2000)-1000)*0.001 , d_x);
d_y = if(beat, (rand(2000)-1000)*0.001 , d_y);
r = if(beat, (rand(2000)-1000)*0.001 , r);
z = if(beat, rand(1000)*0.001 , z);
zoom = 1.01 + z*0.03;
rot = r*0.04;
dx = d_x*0.01;
dy = d_y*0.01;

wave_a = beat;

// nice random hue-fade
rr = rr + (rand(100)-50)/1000;
gg = gg + (rand(100)-50)/1000;
bb = bb + (rand(100)-50)/1000;
rr = max(0, min( 1, rr));
gg = max(0, min( 1, gg));
bb = max(0, min( 1, bb));
mx = max(max(rr,gg),bb);
mn = min(min(rr,gg),bb);
r = (rr-mn)/(mx-mn);
g = (gg-mn)/(mx-mn);
b = (bb-mn)/(mx-mn);


wave_r = r;
wave_g = g;
wave_b = b;

