   return float2( numerator.x*denominator.x + numerator.y*denominator.y,
                  numerator.y*denominator.x - numerator.x*denominator.y)/
          (denominator.x*denominator.x + denominator.y*denominator.y);
}

float2 uv_polar(float2 domain, float2 center){
   float2 c = domain - center;
   float rad_hq = length(c);
   float ang_hq = atan2(c.x,c.y);
   return float2(ang_hq*M_INV_PI_2, rad_hq);
}

float2 uv_polar_logarithmic(float2 domain, float2 center, int fins, float log_factor, float2 coord){
   float2 polar = uv_polar(domain, center);
   return float2(polar.x*fins+coord.x, log_factor*log(polar.y) + coord.y);
}

float2 uv_moebius_transformation(float2 domain, float2 zeroPoint, float2 infinityPoint,float zoom){
   return complex_div((domain - zeroPoint)*zoom, domain - infinityPoint)+0.5;
}

float2 uv_bipolar(float2 domain, float2 northPole, float2 southPole, int fins, float log_factor, float2 coord){
   float2 help_uv = uv_moebius_transformation(domain, northPole, southPole, 1);
   return uv_polar_logarithmic(help_uv,0.5,fins,log_factor,coord);
}


float2 uv_rotate( float2 domain, float2 center, float sinw, float cosw, float scale){
   float2 uv_r = (domain-center);
   return center + float2( cosw*uv_r.x - sinw*uv_r.y, sinw*uv_r.x + cosw*uv_r.y)*scale;
}


