comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;

    float3 d = float3(texsize.zw, 0)*2.5;
    float b1 = lum(GetBlur1(uv + d.xz));
    float b2 = lum(GetBlur1(uv - d.xz));
    float b3 = lum(GetBlur1(uv + d.zy));
    float b4 = lum(GetBlur1(uv - d.zy));

    ret *= 0.5;
    ret += (b1-b2)*3.4*float3(1,0.7,0.3);
    ret += (b3-b4)*3.4*float3(0.2,0.5,0.7);

