frame_eqs_eel:
// -------------------------------- beat detection code ----------------------------------
sure=if(equal(sure,0),.75,sure);
interval=if(equal(interval,0),.7*FPS,interval);
avginterval=if(equal(avginterval,0),.7*FPS,avginterval);
lockinterval=if(equal(lockinterval,0),.7*FPS,lockinterval );
lastbeat=if(equal(lastbeat,0),frame-FPS,lastbeat);
dbass=(bass-pbass)*FPS;
beat=below(dbass,0)*above(pdbass,0)*above(bass,.95*avgbass)*above(frame-lastbeat,FPS/3);
sure=if(beat*below(abs(frame-(interval+lastbeat)),FPS/5),
min(.095+sure,1),beat*(sure-.095)+(1-beat)*sure*.9996);
sure=max(.5,sure);
avgbass=.995*avgbass+.005*bass;
maxdbass=max(maxdbass*.999,dbass);
maxdbass=min(200,maxdbass);
maxdbass=max(.02,maxdbass);
interval=if(beat*below(frame-lastbeat,1.8*interval), ((frame-lastbeat)*.25+.75*interval),interval);
lastbeat=if((1-force)*beat+equal(lastbeat+interval,frame),frame,lastbeat);
avginterval=if(beat,.9*avginterval+.1*interval, avginterval);
lockinterval=if(beat,.9*lockinterval+.1*avginterval,lockinterval);
pbass=bass;
pdbass=dbass;
bpm=60*FPS*.3333*( ((2*(1-sure))/interval)+((2*sure)/avginterval)+(1/lockinterval) );
phase=min( (frame-lastbeat)/(.333*(2*sure*avginterval+2*(1-sure)*interval+lockinterval)), 1 );
beat=if(equal(phase,0),1,beat);
interval=min(interval,.7*FPS);
avginterval=min(avginterval,.7*FPS);
lockinterval=min(lockinterval,.7*FPS);
